import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'mex-com';
  form: FormGroup;
  styleImage = 'rainy';

  constructor(private formBuilder: FormBuilder) {
    this.form = new FormGroup({
      // Define tus controles de formulario aquí...
      // Por ejemplo:
      username: new FormControl(''),
      password: new FormControl(''),
      email: new FormControl('', Validators.required)
    });
  }

  ngOnInit(): void {
    this.buildForm();
  }
  private buildForm(): any {
    this.form = this.formBuilder.group({email:['',[Validators.required, Validators.email]],password:['',[Validators.required,Validators.minLength(6)]]});
  }

  login(event: Event): any {
    event?.preventDefault();
    if (this.form.valid){
      const value = this.form.value;
      console.log(`'%CUSER: '${value.email} - PASSWORD: ${value.password}`,'background: #222; color: #bada55');
    }
  }

  unsplashClass() {
    return {
      'min-height': '100%',
      'background-color': `url(https://source.unsplash.com/random/1200x900?"${this.styleImage}") no-repeat center center`,
      'background-size': 'cover',
      position: 'relative'
    };
  }

  checkRequiredError(): boolean {
    return this.form.controls['email'].hasError('required');
  }
}
