import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'mex-com';
  form: FormGroup;
  styleImage = 'rains';

  constructor() {
    this.form = new FormGroup({
      // Define tus controles de formulario aquí...
      // Por ejemplo:
      username: new FormControl(''),
      password: new FormControl(''),
      email: new FormControl('', Validators.required)
    });
  }

  login(event: any) {
    // Lógica de inicio de sesión...
  }

  unsplashClass() {
    return {
      'min-height': '100%',
      'background-color': `url(https://source.unsplash.com/random/1200x900?"${this.styleImage}") no-repeat center center`,
      'background-size': 'cover',
      // Otros estilos...
    };
  }

  checkRequiredError(): boolean {
    return this.form.controls['email'].hasError('required');
  }
}
