import express  from "express";
import  morgan  from "morgan";
import MEXCOMrutas from "./routes/MEXCOMAPI";
//Expres nos ayuda a manejar rutas como get, put, delete
const app = express();

//Settings 
app.set("port", 9000);

//Middleware
//Es una petición intermedia entre la peticion y la respuesta en este caso usamos el dev mode
app.use(morgan("dev"));

//Implementamos las rutas de peticion REST por defualt 
app.use("/api/puntosOferta", MEXCOMrutas);

export default app;

